import{j as e,r as a,i,A as t,C as n,D as s,E as r,G as o}from"./vendor-DOxSvNLs.js";import{M as l}from"./index-FCfXzLza.js";const d=()=>e.jsxs("section",{"data-testid":"introduction",className:"w-full max-w-3xl text-center mb-10 pt-4","aria-labelledby":"about-heading",children:[e.jsx("h1",{id:"about-heading",className:"text-4xl font-bold mb-4",children:"About Me"}),e.jsx("p",{className:"text-lg",children:"I'm a full-stack software engineer with a background in platform and site reliability engineering in media and video streaming. I specialize in building high-quality user experiences using modern web technologies, and highly scalable, reliable cloud software for large, global platforms."})]}),c=({date:a,title:i,description:t,position:n,testId:s="timeline-item"})=>e.jsxs("li",{"data-testid":s,tabIndex:0,role:"listitem",children:[e.jsx("div",{className:"timeline-middle",children:e.jsx("svg",{"data-testid":"decorative-icon","aria-hidden":"true",className:"h-5 w-5",fill:"currentColor",viewBox:"0 0 20 20",xmlns:"http://www.w3.org/2000/svg",children:e.jsx("path",{fillRule:"evenodd",d:"M10 18a8 8 0 100-16 8 8 0 000 16zm3.857-9.809a.75.75 0 00-1.214-.882l-3.483 4.79-1.88-1.88a.75.75 0 10-1.06 1.061l2.5 2.5a.75.75 0 001.137-.089l4-5.5z",clipRule:"evenodd"})})}),e.jsxs("div",{className:`timeline-${n} mb-10 md:text-${"start"===n?"end":"start"}`,children:[e.jsx("time",{className:"font-mono italic",children:a}),e.jsx("h3",{className:"text-lg font-black",children:i}),e.jsx("p",{role:"article",children:t})]}),e.jsx("hr",{})]}),m=({timelineData:a})=>{const i=a||[{date:"2024 - 2025",title:"Senior Software Engineer - Crunchyroll",description:"Developed the next generation video streaming experience for millions of concurrent users during high-traffic content releases like Demon Slayer, Kaiju No. 8, and Dragon Ball Daima.",position:"start"},{date:"2021 - 2023",title:"Staff Software Engineer - Disney Research",description:"Using AI/ML, automated video post-production and quality control for Disney, Lucasfilm, Marvel, and Pixar films, enhancing theatrical and streaming experiences on Disney+ and Hulu.",position:"end"},{date:"2019 - 2021",title:"Lead Software Engineer - HPE Services",description:"Developed cloud self-service platforms, enabling enterprises to deploy and manage infrastructure faster, reducing downtime and improving scalability for business-critical applications.",position:"start"},{date:"2016 - 2019",title:"Senior DevOps Engineer - Rackspace",description:"Optimized cloud deployments for enterprise clients, improving reliability, reducing costs, and ensuring seamless application performance for millions of end users.",position:"end"},{date:"2013 - 2016",title:"Senior System Engineer - U.S. Department of Veterans Affairs",description:"Improved healthcare system reliability and security, ensuring medical staff could provide care without disruption and patients had uninterrupted access to critical medical services.",position:"start"},{date:"2012 - 2016",title:"Bachelor of Science in Computer Science - University of Texas at Austin",description:"Graduated with honors and completed undergraduate research on unmanned space exploration using hydrogen fuel cell technology with NASA.",position:"start"},{date:"2005 - 2013",title:"Systems Engineer - U.S. Army",description:"Maintained secure communications systems and network infrastructure for military operations.",position:"end"},{date:"2004 - 2005",title:"System Administrator - CompUSA",description:"Managed network infrastructure and system administration for retail operations.",position:"start"}];return e.jsxs("div",{className:"w-full max-w-5xl mb-10 p-6 bg-base-100 rounded-lg shadow-lg",children:[e.jsx("h2",{className:"text-3xl font-semibold text-center mb-6",children:"Career Timeline"}),e.jsx("ul",{className:"timeline timeline-snap-icon max-md:timeline-compact timeline-vertical","aria-label":"Career Timeline",children:i.map(((a,i)=>e.jsx(c,{date:a.date,title:a.title,description:a.description,position:a.position},i)))})]})},h=()=>{var d;const[c,m]=a.useState(null),[h,u]=a.useState(!1),[g,p]=a.useState(!0),[f,y]=a.useState(0),b=[{id:"software-engineering-modal",category:"Software Engineering",icon:e.jsx(i,{className:"text-4xl text-info mb-4"}),content:e.jsxs(e.Fragment,{children:[e.jsx("p",{className:"leading-relaxed mb-6",children:"My journey in software engineering started well before it became my profession. Back in the early days of the web, I was inspired by my love for video games and anime, which led me to build my first websites. In 1996, I was using tools like Microsoft FrontPage, Netscape Composer, GeoCities, and Tripod to experiment with web development. By high school in 2002, I had upgraded to Macromedia Dreamweaver, which really solidified my interest in coding and systems design. It wasn't just a hobby anymore – it was something I knew I wanted to do long-term."}),e.jsx("p",{className:"leading-relaxed mb-6",children:"Over the years, my career has evolved into full-stack development across different industries, always with a focus on creating systems that are scalable, maintainable, and high-performance. I'm a big advocate of domain-driven design (DDD), which helps me align technical implementations with the real needs of the business. This approach lets me build around specific business logic, encapsulating it within bounded contexts to keep everything clean and organized."}),e.jsx("h4",{className:"font-bold text-lg mb-3",children:"Frontend Development"}),e.jsx("p",{className:"leading-relaxed mb-6",children:"When it comes to frontend development, I prefer React because it allows me to build reusable, composable components that simplify development, especially in larger applications. For bundling, I like Vite for its fast Hot Module Replacement (HMR) and simple setup, which is particularly useful in TypeScript projects where real-time feedback is critical."}),e.jsx("p",{className:"leading-relaxed mb-6",children:"For styling, I often combine Tailwind CSS with DaisyUI. Tailwind's utility-first approach lets me quickly prototype UI elements without diving into custom CSS, while DaisyUI provides accessible, pre-styled components that ensure a consistent look and feel across the application."}),e.jsx("p",{className:"leading-relaxed mb-6",children:"Accessibility is a priority for me because it's simply a non-negotiable part of modern web design. I make sure every interface works seamlessly across devices and assistive technologies by implementing ARIA roles, keyboard navigation, and screen reader support. I also bake in internationalization (i18n) from the start, using tools like react-i18next to make localization easy for global users."}),e.jsx("p",{className:"leading-relaxed mb-6",children:"For testing, I go with Vitest (or Jest) for unit and integration tests, especially given Vitest's fast integration with Vite. I also use React Testing Library to verify that my components behave as expected. On top of that, DataDog RUM and Google Analytics help me track real user interactions, which I use to continuously improve both the user experience and performance."}),e.jsx("h4",{className:"font-bold text-lg mb-3",children:"Backend Development"}),e.jsx("p",{className:"leading-relaxed mb-6",children:"On the backend, I focus on building scalable microservices using the right language and framework for the job. Often, I work in TypeScript because many of my projects integrate closely with frontend services, and TypeScript's strict type safety helps minimize runtime errors while keeping development fast and maintainable."}),e.jsx("p",{className:"leading-relaxed mb-6",children:"For high-performance systems – whether it's a gRPC or REST API, media transcoding pipeline, or a real-time application – I'll opt for Go. It handles concurrency really well and performs efficiently under heavy loads. If I'm dealing with more flexible workflows, like data pipelines or machine learning tasks, I lean toward Python because its vast ecosystem and tools like Apache Airflow and Spark make it easy to work with large datasets or complex processes."}),e.jsx("p",{className:"leading-relaxed mb-6",children:"Data handling is something I take seriously, and I use both SQL and NoSQL databases depending on the project. I tend to choose PostgreSQL or DynamoDB based on the data model and use Redis for caching. I often integrate with messaging systems like SQS or Kafka for handling async workflows."}),e.jsx("p",{className:"leading-relaxed mb-6",children:"I like to follow clean architecture principles, focusing on modularity and separation of concerns. This ensures that the codebase stays maintainable and flexible as new features or requirements come up. To monitor everything, I use DataDog and Prometheus for real-time insights into service performance, capturing key metrics like response times, memory usage, and error rates. This helps me spot bottlenecks early and optimize proactively."}),e.jsx("h4",{className:"font-bold text-lg mb-3",children:"DevOps"}),e.jsx("p",{className:"leading-relaxed mb-6",children:"I've been working with various version control and CI/CD platforms since 2016, and GitHub and GitLab have become my preferred tools for managing code and automating workflows. I build CI/CD pipelines that cover everything—from linting and testing to deployment and monitoring. I also integrate security scanning tools like Snyk and Veracode to identify vulnerabilities early on."}),e.jsx("p",{className:"leading-relaxed mb-6",children:"For release automation, I use Semantic Release with Conventional Commits to ensure that versioning is consistent and releases are automated without any hiccups."}),e.jsx("p",{className:"leading-relaxed mb-6",children:"Containerization is a big part of my workflow. I use Docker for local development, especially with LocalStack when building AWS-based services. In production, Kubernetes is my go-to for orchestration, particularly when paired with ArgoCD, Argo Rollouts, Helm, and Istio."}),e.jsx("p",{className:"leading-relaxed mb-6",children:"I place a strong emphasis on observability. DataDog is my preferred tool for centralized logs, metrics, and traces because it gives me real-time insights into how systems are performing and how users are interacting with services. DataDog's dashboards and alerting capabilities make it easy to track key metrics and quickly resolve any issues before they impact users. I also integrate OpsGenie or PagerDuty for incident management, making sure that issues are addressed efficiently."})]})},{id:"cloud-infrastructure-modal",category:"Cloud Infrastructure",icon:e.jsx(t,{className:"text-4xl text-info mb-4"}),content:e.jsxs(e.Fragment,{children:[e.jsx("p",{className:"leading-relaxed mb-6",children:"My experience with cloud infrastructure has evolved from managing complex hybrid systems to designing multi-cloud solutions for large-scale production workloads. I've spent a lot of time working with AWS, Azure, and GCP, always looking for ways to leverage cloud-native services to improve scalability, security, and cost efficiency."}),e.jsx("h4",{className:"font-bold text-lg mb-3",children:"Infrastructure as Code (IaC)"}),e.jsx("p",{className:"leading-relaxed mb-6",children:"Using tools like Pulumi, Terraform, and Serverless Framework, my preferred approach to IaC emphasizes using pre-built modules from within the service repository — this way, the infrastructure can scale quickly while still maintaining high security and performance standards. The goal is to ensure the infrastructure can evolve alongside the application and business requirements without introducing complexity or risk."}),e.jsx("h4",{className:"font-bold text-lg mb-3",children:"Cloud Infrastructure Monitoring"}),e.jsx("p",{className:"leading-relaxed mb-6",children:"I prefer DataDog and cloud-native solutions to track infrastructure performance and reliability. With DataDog, custom dashboards and alerts help me keep an eye on key metrics like resource utilization, latency, and network traffic so I can detect and fix issues proactively before they turn into bigger problems, which is critical for minimizing downtime. I also tie these monitoring tools into automated incident response workflows with OpsGenie or PagerDuty to make sure any issues are handled quickly and efficiently."}),e.jsx("h4",{className:"font-bold text-lg mb-3",children:"Networking"}),e.jsx("p",{className:"leading-relaxed mb-6",children:"I've worked a lot with VPC architectures to ensure secure and efficient connectivity between services. This has included setting up secure networking solutions like DirectConnect, VPNs, and fine-tuning IAM roles to make sure resources are both secure and accessible only to authorized users. I also make sure that network traffic and security events are continuously monitored, so any threats can be detected and responded to before they become an issue."})]})},{id:"media-engineering-modal",category:"Media Engineering",icon:e.jsx(n,{className:"text-4xl text-info mb-4"}),content:e.jsxs(e.Fragment,{children:[e.jsx("p",{className:"leading-relaxed mb-6",children:"Media engineering has been a core part of my work, especially with platforms like Disney+ and Crunchyroll. My focus has been on building and optimizing media transcoding, delivery, and playback to ensure video content is processed and delivered efficiently, no matter the device."}),e.jsx("h4",{className:"font-bold text-lg mb-3",children:"Transcoding"}),e.jsx("p",{className:"leading-relaxed mb-6",children:"I've built and fine-tuned media transcoding pipelines using tools like FFmpeg and cloud services such as AWS Elemental MediaConvert, automating transcoding tasks and ensuring efficient delivery across a wide range of devices. Maintaining quality is a big priority for me — I work to ensure videos maintain high resolution and perform well, even when network conditions aren't ideal."}),e.jsx("h4",{className:"font-bold text-lg mb-3",children:"Delivery"}),e.jsx("p",{className:"leading-relaxed mb-6",children:"Smooth, efficient media delivery is essential for a great user experience. I've worked extensively with adaptive bitrate streaming technologies like HLS and DASH to make sure content is optimized for different devices and network conditions. I've also built and integrated Content Delivery Networks (CDNs) on Akamai, Fastly, and AWS CloudFront to reduce latency and offload traffic, ensuring that users can access content quickly and reliably, no matter where they're located."}),e.jsx("h4",{className:"font-bold text-lg mb-3",children:"Playback"}),e.jsx("p",{className:"leading-relaxed mb-6",children:"For me, optimizing video playback is all about giving users the best experience possible, no matter what device they're on or what their network situation looks like. I've implemented adaptive bitrate (ABR) streaming, which adjusts video quality dynamically based on available bandwidth, providing a smoother, more consistent viewing experience even when network conditions change. I constantly monitor playback performance metrics—things like buffering events and user feedback—to proactively address any issues and ensure that playback remains high-quality at all times."})]})},{id:"machine-learning-modal",category:"Machine Learning",icon:e.jsx(s,{className:"text-4xl text-info mb-4"}),content:e.jsxs(e.Fragment,{children:[e.jsx("p",{className:"leading-relaxed mb-6",children:"My work in machine learning focuses on solving practical problems like video analysis, recommendation systems, film production, and automation through AI/ML models. Over the years, I've collaborated with research teams to take models from development all the way to production, using frameworks like TensorFlow and PyTorch. I've built end-to-end machine learning pipelines, managed model lifecycles, and deployed scalable solutions that handle real-time data processing."}),e.jsx("h4",{className:"font-bold text-lg mb-3",children:"Frameworks & Tools"}),e.jsx("p",{className:"leading-relaxed mb-6",children:"I've primarily worked with computer vision models like U-Net, GroundingDINO, YOLO, Segment Anything, and VMAF. For training and deploying these models, I use TensorFlow and PyTorch. Whether it's for video analysis, film production, or content recommendation, I ensure the models are optimized for performance and scalability. In addition to using AWS SageMaker for model management, I also work with Weights & Biases and MLflow for experiment tracking and model lifecycle management. For monitoring system-level performance, infrastructure health, and model drift, I lean on DataDog and SageMaker Model Monitor to ensure models stay reliable and continue to meet evolving business needs."}),e.jsx("h4",{className:"font-bold text-lg mb-3",children:"MLOps & Deployment"}),e.jsx("p",{className:"leading-relaxed mb-6",children:"Managing the full lifecycle of machine learning models requires a solid MLOps strategy. I lean on tools like MLflow, Weights & Biases, Kubernetes, and AWS SageMaker to automate and streamline processes from model training to evaluation and deployment. By setting up robust pipelines, I ensure that models are continuously monitored and retrained as new data becomes available, keeping them adaptable and effective."}),e.jsx("h4",{className:"font-bold text-lg mb-3",children:"Data Pipelines for ML"}),e.jsx("p",{className:"leading-relaxed mb-6",children:"Building reliable data pipelines is essential to any machine learning project. I work with tools like Apache Kafka, Spark, and Airflow to maintain efficient, real-time data flows that feed into the models. This ensures both training and production data are processed accurately and on time. Reliable, high-performing data pipelines are key to supporting real-time analytics and decision-making, and I continuously monitor them to ensure seamless operation across different environments."})]})},{id:"data-engineering-modal",category:"Data Engineering",icon:e.jsx(r,{className:"text-4xl text-info mb-4"}),content:e.jsxs(e.Fragment,{children:[e.jsx("p",{className:"leading-relaxed mb-6",children:"Data engineering has always been a critical part of my work, especially when it comes to building scalable ETL pipelines that support real-time data processing and analytics. I've designed and managed systems that handle large datasets, focusing on high availability, data integrity, and optimizing data flows to power business-critical applications, machine learning models, and reporting systems. Keeping these pipelines reliable, accurate, and high-performing is always top of mind, with continuous monitoring to ensure uptime and performance."}),e.jsx("h4",{className:"font-bold text-lg mb-3",children:"Data Pipelines"}),e.jsx("p",{className:"leading-relaxed mb-6",children:"I build data pipelines using tools like Apache Kafka, Airflow, and Spark to handle both real-time and batch data processing. My main goal is to ensure that data flows smoothly from source to destination with minimal latency and maximum accuracy. Whether it's supporting analytics, machine learning workflows, or day-to-day business operations, I make sure the pipelines are efficient and reliable. Monitoring and alerting are essential to keeping the systems running smoothly and ensuring real-time data is processed without bottlenecks."}),e.jsx("h4",{className:"font-bold text-lg mb-3",children:"Data Warehousing"}),e.jsx("p",{className:"leading-relaxed mb-6",children:"I have hands-on experience designing and managing data warehouses using platforms like AWS Redshift and Azure Synapse. My work involves creating efficient schemas, optimizing queries for performance, and ensuring the data is structured to support both transactional and analytical workloads. I keep a close eye on performance metrics and usage patterns, making sure that queries are running efficiently and that data storage is cost-optimized for the long term."}),e.jsx("h4",{className:"font-bold text-lg mb-3",children:"Data Governance & Quality"}),e.jsx("p",{className:"leading-relaxed mb-6",children:"Maintaining trust in data-driven decisions starts with data quality and governance. I implement data governance frameworks to ensure that data is secure, high-quality, and accessible throughout the pipeline. This includes defining data standards, applying access controls, and setting up quality checks to catch issues early. Continuous monitoring is essential to ensure compliance with governance policies and to maintain data integrity at every stage of the process."})]})},{id:"security-modal",category:"Security",icon:e.jsx(o,{className:"text-4xl text-info mb-4"}),content:e.jsxs(e.Fragment,{children:[e.jsx("p",{className:"leading-relaxed mb-6",children:"Security has always been in the fabric of my work, whether I'm building cloud software, deploying machine learning models, or managing media and data engineering pipelines. I've designed and implemented security strategies that protect sensitive data across various systems, ensuring compliance with industry standards like HIPAA and PCI-DSS. From configuring secure cloud architectures to managing vulnerability assessments and incident response, my goal is always to keep systems secure, reliable, and compliant. Continuous security monitoring has been key to identifying and responding to potential threats proactively."}),e.jsx("h4",{className:"font-bold text-lg mb-3",children:"Application Security"}),e.jsx("p",{className:"leading-relaxed mb-6",children:"Ensuring security is embedded in the software development process is a key part of my work, starting from the earliest stages of building software. I incorporate security best practices into the codebase, such as secure coding standards, dependency management, and continuous vulnerability assessment. A key aspect of this is generating and maintaining a Software Bill of Materials (SBOM) to ensure full visibility into the open-source components and dependencies used within the software. This helps identify potential risks, such as outdated or vulnerable libraries, before they reach production."}),e.jsx("p",{className:"leading-relaxed mb-6",children:"I also use security in the CI/CD process to ensure that code is thoroughly tested and vetted before deployment. Throughout the pipeline, I use tools like Snyk and Veracode to ensure that security checks are automated and performed early in the development cycle."}),e.jsx("h4",{className:"font-bold text-lg mb-3",children:"Cloud Security"}),e.jsx("p",{className:"leading-relaxed mb-6",children:"Securing cloud environments is one of the core aspects of my work. I focus on practices like role-based access control (RBAC), encryption (at-rest, in-use, and in-transit), and multi-factor authentication (MFA) to protect resources in AWS and Azure environments. I've implemented security measures like IAM roles, key management systems, and secure networking configurations, ensuring that cloud resources are both secure and compliant with industry regulations. Using DataDog and AWS CloudTrail, I'm able to detect potential security incidents early and respond quickly to mitigate them."}),e.jsx("h4",{className:"font-bold text-lg mb-3",children:"Network Security"}),e.jsx("p",{className:"leading-relaxed mb-6",children:"In terms of network security, I've designed secure network architectures using VPCs, firewalls, and VPNs to ensure data remains protected in transit. My focus has been on zero-trust network principles, where no entity is trusted by default. By setting up continuous monitoring, I ensure that any threats are detected early and mitigated before they escalate. Whether it's securing real-time video streams or ensuring data pipelines are protected, network security plays a critical role in my work across various domains."}),e.jsx("h4",{className:"font-bold text-lg mb-3",children:"Vulnerability Management & Compliance"}),e.jsx("p",{className:"leading-relaxed mb-6",children:"Managing vulnerabilities and ensuring compliance with standards like NIST, FedRAMP, and SOC 2 has been a big part of my work, especially in regulated industries like government, finance, and healthcare. I've used tools like Snyk, Veracode, and AWS Inspector to identify and remediate security risks in both cloud-based applications and machine learning pipelines. Continuous vulnerability scanning and real-time monitoring have been essential for keeping systems secure and compliant, ensuring rapid response to potential threats as they arise."})]})}];return e.jsxs("div",{className:"w-full max-w-5xl mb-10 p-6 bg-base-100 rounded-lg shadow-lg",children:[e.jsx("h2",{className:"text-2xl font-semibold mb-6 text-center",children:"Skills"}),e.jsx("div",{className:"grid grid-cols-2 sm:grid-cols-3 gap-6",children:b.map(((a,i)=>e.jsxs("div",{className:"flex flex-col items-center text-center",children:[a.icon,e.jsx("h3",{className:"font-semibold mb-2",children:a.category}),e.jsx("button",{className:"btn btn-sm mt-4",onClick:()=>(e=>{m(e),u(!0)})(i),children:"Learn More"})]},a.id)))}),null!==c&&e.jsx(l,{isOpen:h,onClose:()=>{u(!1)},title:b[c].category,content:null==(d=b[c])?void 0:d.content,onNext:()=>{m((e=>null===e?0:(e+1)%b.length))},onPrevious:()=>{m((e=>null===e?b.length-1:(e-1+b.length)%b.length))},totalPages:b.length,currentPage:c,isExpanded:g,onToggleExpand:()=>{p(!g),y((e=>e+1))},triggerOverflowCheck:f})]})},u=()=>e.jsxs("main",{className:"flex flex-col items-center justify-center w-full p-6",role:"main","aria-label":"About Me Section",children:[e.jsx(d,{}),e.jsx(h,{}),e.jsx(m,{})]});export{u as default};
